import React, { useState } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import './Home.css';
import Login from './Login'; // Login bileşeni

const Home = () => {
    const [isModalOpen, setIsModalOpen] = useState(false);

    const handleOpenModal = () => {
        setIsModalOpen(true);
    };

    const handleCloseModal = () => {
        setIsModalOpen(false);
    };

    return (
        <div className="container-fluid">
            <div className="row">
                {/* Sol Reklam Alanı (2 grid) */}
                <div className="col-md-2">
                    <div className="advertisement">
                        <h4>Reklam Alanı 1</h4>
                    </div>
                </div>

                {/* Para Birimleri ve Register (8 grid) */}
                <div className="col-md-8">
                    <div className="row no-gutters">
                        {/* Para birimi kutuları (6 adet 2 grid) */}
                        <div className="col-md-2">
                            <div className="currency-box">Para Birimi 1</div>
                        </div>
                        <div className="col-md-2">
                            <div className="currency-box">Para Birimi 2</div>
                        </div>
                        <div className="col-md-2">
                            <div className="currency-box">Para Birimi 3</div>
                        </div>
                        <div className="col-md-2">
                            <div className="currency-box">Para Birimi 4</div>
                        </div>
                        <div className="col-md-2">
                            <div className="currency-box">Para Birimi 5</div>
                        </div>
                        <div className="col-md-2">
                            <button className="register-box" onClick={handleOpenModal}>
                                Register
                            </button>
                        </div>
                    </div>

                    {/* Para grafiği (2 grid altında) */}
                    <div className="chart-section">
                        <h4>Para Birimi Grafiği</h4>
                    </div>
                </div>

                {/* Sağ Reklam Alanı (2 grid) */}
                <div className="col-md-2">
                    <div className="advertisement">
                        <h4>Reklam Alanı 2</h4>
                    </div>
                </div>
            </div>

            {/* Modal */}
            {isModalOpen && (
                <div className="modal-overlay">
                    <div className="modal-content">
                        <button className="close-button" onClick={handleCloseModal}>
                            &times; {/* Kapatma simgesi */}
                        </button>
                        <Login /> {/* Login bileşenini burada kullan */}
                    </div>
                </div>
            )}
        </div>
    );
};

export default Home;
