import React, { useState } from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import "./Home.css";
import Login from "./Login"; // Login bileşeni
import { CSSTransition } from 'react-transition-group'; // Animasyon için
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";

const Home = () => {
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [isLoginMode, setIsLoginMode] = useState(true); // Login ve Register modunu yönetir
  const [isLoggedIn, setIsLoggedIn] = useState(false); // Giriş yapılmış mı onu kontrol et

  const handleOpenModal = () => {
    setIsModalOpen(true); // Modalı aç
  };

  const handleCloseModal = () => {
    setIsModalOpen(false); // Modalı kapat
  };

  const toggleMode = (mode) => {
    setIsLoginMode(mode === 'login'); // Login ve Register arasında geçiş yapar
  };

  return (
    <div className="container-fluid">
      <div className="row">
        <div className="col-md-2">
          <div className="advertisement">
            <h4>Reklam Alanı 1</h4>
          </div>
        </div>

        <div className="col-md-8">
          <div className="row no-gutters">
            {/* Para birimi kutuları ve Giriş/Kayıt Ol butonu */}
            {/* ... Para birimleri kodu ... */}
            <div className="col-md-2">
              <button
                className="register-box"
                onClick={handleOpenModal}
              >
                {isLoggedIn ? "Profilim" : "Giriş Yap/Kayıt Ol"}
              </button>
            </div>
          </div>
          <div className="chart-section">
            <h4>Para Birimi Grafiği</h4>
          </div>
        </div>

        <div className="col-md-2">
          <div className="advertisement">
            <h4>Reklam Alanı 2</h4>
          </div>
        </div>
      </div>

      {isModalOpen && (
        <div className="modal-overlay">
          <div className="modal-content">
            <button className="close-button" onClick={handleCloseModal}>
              &times;
            </button>
            <div className="selection-buttons d-flex justify-content-center">
              <button
                className={`btn ${isLoginMode ? 'active' : ''}`}
                onClick={() => toggleMode('login')}
              >
                Giriş Yap
              </button>
              <button
                className={`btn ${!isLoginMode ? 'active' : ''}`}
                onClick={() => toggleMode('register')}
              >
                Kayıt Ol
              </button>
            </div>
            <CSSTransition
              in={isModalOpen}
              timeout={300}
              classNames="fade"
              unmountOnExit
            >
              <Login
                isLoginMode={isLoginMode}
                onRequestClose={handleCloseModal}
                setIsLoggedIn={setIsLoggedIn}
              />
            </CSSTransition>
          </div>
        </div>
      )}
    </div>
  );
};

export default Home;
