import React from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom';
import Home from './Home'; // Home bileşeni
import Login from './Login'; // Login bileşeni

function App() {
  return (
    <Router>
      <Routes>
        {/* Varsayılan olarak "/login" adresine yönlendir */}
        <Route path="/" element={<Navigate to="/login" />} />

        {/* Diğer sayfalar */}
        <Route path="/home" element={<Home />} /> {/* Eğer başka sayfalar varsa */}
        <Route path="/login" element={<Login />} /> {/* Login sayfası */}
      </Routes>
    </Router>
  );
}

export default App;
