import React, { useState } from "react";
import axios from "axios";
import "./Login.css";

const Login = ({ isOpen, onRequestClose, isRegister, setIsRegister, setIsLoggedIn }) => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [name, setName] = useState("");
  const [passwordConfirmation, setPasswordConfirmation] = useState("");
  const [error, setError] = useState(null);
  const [successMessage, setSuccessMessage] = useState(null);

  if (!isOpen) return null;

  const handleLogin = async (e) => {
    e.preventDefault();
    setError(null);
    setSuccessMessage(null);
    try {
      await axios.post("http://127.0.0.1:8000/api/login", { email, password });
      setSuccessMessage("Giriş başarılı!");
      setIsLoggedIn(true);
      onRequestClose();
    } catch (error) {
      setError(error.response?.data.message || "Giriş hatalı.");
    }
  };

  const handleRegister = async (e) => {
    e.preventDefault();
    setError(null);
    setSuccessMessage(null);
    try {
      await axios.post("http://127.0.0.1:8000/api/register", {
        name,
        email,
        password,
        password_confirmation: passwordConfirmation,
      });
      setSuccessMessage("Kayıt başarılı!");
      onRequestClose();
    } catch (error) {
      setError(error.response?.data.message || "Kayıt hatası.");
    }
  };

  return (
    <div className="modal-overlay">
      <div className="login-modal">
        <div className="toggle-section">
          <h6>
            <span onClick={() => setIsRegister(false)}>Log In</span> | 
            <span onClick={() => setIsRegister(true)}>Sign Up</span>
          </h6>
        </div>
        <form onSubmit={isRegister ? handleRegister : handleLogin} className="form">
          <h4>{isRegister ? "Sign Up" : "Log In"}</h4>
          {error && <div className="alert alert-danger">{error}</div>}
          {successMessage && <div className="alert alert-success">{successMessage}</div>}
          {isRegister && (
            <input
              type="text"
              placeholder="Your Name"
              value={name}
              onChange={(e) => setName(e.target.value)}
              required
            />
          )}
          <input
            type="email"
            placeholder="Your Email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
          <input
            type="password"
            placeholder="Your Password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
          {isRegister && (
            <input
              type="password"
              placeholder="Confirm Password"
              value={passwordConfirmation}
              onChange={(e) => setPasswordConfirmation(e.target.value)}
              required
            />
          )}
          <button type="submit" className="btn">Submit</button>
        </form>
        <button className="close-btn" onClick={onRequestClose}>X</button>
      </div>
    </div>
  );
};

export default Login;
