import React, { useState } from 'react';
import axios from 'axios';
import './Login.css'; // CSS dosyasını içe aktar

const Login = ({ isOpen, onRequestClose, isRegister, setIsLoggedIn }) => {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [name, setName] = useState('');
    const [passwordConfirmation, setPasswordConfirmation] = useState('');
    const [error, setError] = useState(null);
    const [successMessage, setSuccessMessage] = useState(null);
    const [isLoginMode, setIsLoginMode] = useState(!isRegister); // Slider ile login/register arasında geçiş

    const handleLogin = async (e) => {
        e.preventDefault();
        setError(null);
        setSuccessMessage(null);

        try {
            const response = await axios.post('http://127.0.0.1:8000/api/login', {
                email,
                password,
            });
            console.log(response.data);
            setSuccessMessage('Login successful!');
            setIsLoggedIn(true); // Giriş başarılı olduğunda durumu güncelle
            onRequestClose(); // Modalı kapat
        } catch (error) {
            if (error.response) {
                setError(error.response.data.message || 'Invalid credentials');
            } else {
                setError('Network error. Please try again later.');
            }
        }
    };

    const handleRegister = async (e) => {
        e.preventDefault();
        setError(null);
        setSuccessMessage(null);

        try {
            const response = await axios.post('http://127.0.0.1:8000/api/register', {
                name,
                email,
                password,
                password_confirmation: passwordConfirmation,
            });
            console.log(response.data);
            setSuccessMessage('Registration successful!');
            onRequestClose(); // Kayıt başarılı olduğunda modalı kapat
        } catch (error) {
            if (error.response) {
                setError(error.response.data.message || 'Registration failed');
            } else {
                setError('Network error. Please try again later.');
            }
        }
    };

    const toggleMode = () => {
        setIsLoginMode(!isLoginMode); // Giriş ve kayıt modları arasında geçiş yap
    };

    return (
        <div className="login-modal">
            <h2>{isLoginMode ? 'Login' : 'Register'}</h2>
            {error && <div className="error">{error}</div>}
            {successMessage && <div className="success">{successMessage}</div>}

            {/* Slider için buton */}
            <button className="toggle-button" onClick={toggleMode}>
                {isLoginMode ? 'Kayıt Ol' : 'Giriş Yap'}
            </button>

            <form onSubmit={isLoginMode ? handleLogin : handleRegister}>
                {!isLoginMode && (
                    <div>
                        <input
                            type="text"
                            placeholder="Name"
                            value={name}
                            onChange={(e) => setName(e.target.value)}
                            required
                        />
                    </div>
                )}
                <div>
                    <input
                        type="email"
                        placeholder="Email"
                        value={email}
                        onChange={(e) => setEmail(e.target.value)}
                        required
                    />
                </div>
                <div>
                    <input
                        type="password"
                        placeholder="Password"
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                        required
                    />
                </div>
                {!isLoginMode && (
                    <div>
                        <input
                            type="password"
                            placeholder="Confirm Password"
                            value={passwordConfirmation}
                            onChange={(e) => setPasswordConfirmation(e.target.value)}
                            required
                        />
                    </div>
                )}
                <button type="submit">{isLoginMode ? 'Login' : 'Register'}</button>
            </form>
        </div>
    );
};

export default Login;
