import React, { useState } from 'react';
import axios from 'axios';
import './Login.css'; // CSS dosyasını içe aktar

const Login = ({ isOpen, onRequestClose, isRegister, setIsLoggedIn }) => {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [name, setName] = useState('');
    const [passwordConfirmation, setPasswordConfirmation] = useState('');
    const [error, setError] = useState(null);
    const [successMessage, setSuccessMessage] = useState(null);

    const handleLogin = async (e) => {
        e.preventDefault();
        setError(null);
        setSuccessMessage(null);

        try {
            const response = await axios.post('http://127.0.0.1:8000/api/login', {
                email,
                password,
            });
            console.log(response.data);
            setSuccessMessage('Login successful!');
            setIsLoggedIn(true); // Giriş başarılı olduğunda durumu güncelle
            // Giriş başarılı olduğunda modalı kapat
            onRequestClose();
        } catch (error) {
            if (error.response) {
                setError(error.response.data.message || 'Invalid credentials');
            } else {
                setError('Network error. Please try again later.');
            }
        }
    };

    const handleRegister = async (e) => {
        e.preventDefault();
        setError(null);
        setSuccessMessage(null);

        try {
            const response = await axios.post('http://127.0.0.1:8000/api/register', {
                name,
                email,
                password,
                password_confirmation: passwordConfirmation,
            });
            console.log(response.data);
            setSuccessMessage('Registration successful!');
            // Kayıt başarılı olduğunda modalı kapat
            onRequestClose();
        } catch (error) {
            if (error.response) {
                setError(error.response.data.message || 'Registration failed');
            } else {
                setError('Network error. Please try again later.');
            }
        }
    };

    return (
        isOpen && ( // Bu koşulda sadece modal açıkken render edilir.
            <div>
                <h2>{isRegister ? 'Register' : 'Login'}</h2>
                {error && <div className="error">{error}</div>}
                {successMessage && <div className="success">{successMessage}</div>}
                <form onSubmit={isRegister ? handleRegister : handleLogin}>
                    {isRegister && (
                        <div>
                            <input
                                type="text"
                                placeholder="Name"
                                value={name}
                                onChange={(e) => setName(e.target.value)}
                                required
                            />
                        </div>
                    )}
                    <div>
                        <input
                            type="email"
                            placeholder="Email"
                            value={email}
                            onChange={(e) => setEmail(e.target.value)}
                            required
                        />
                    </div>
                    <div>
                        <input
                            type="password"
                            placeholder="Password"
                            value={password}
                            onChange={(e) => setPassword(e.target.value)}
                            required
                        />
                    </div>
                    {isRegister && (
                        <div>
                            <input
                                type="password"
                                placeholder="Confirm Password"
                                value={passwordConfirmation}
                                onChange={(e) => setPasswordConfirmation(e.target.value)}
                                required
                            />
                        </div>
                    )}
                    <button type="submit">{isRegister ? 'Register' : 'Login'}</button>
                </form>
            </div>
        )
    );
};

export default Login;
