import React, { useState } from 'react';
import axios from 'axios';
import './Login.css'; // CSS dosyasını içe aktar

const Login = () => {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [name, setName] = useState('');
    const [passwordConfirmation, setPasswordConfirmation] = useState('');
    const [isRegistering, setIsRegistering] = useState(false);
    const [error, setError] = useState(null);
    const [successMessage, setSuccessMessage] = useState(null);

    const handleLogin = async (e) => {
        e.preventDefault();
        setError(null);
        setSuccessMessage(null);

        try {
            const response = await axios.post('http://127.0.0.1:8000/api/login', {
                email,
                password,
            });
            console.log(response.data);
            setSuccessMessage('Login successful!');
            // Giriş başarılı olduğunda, kullanıcının token veya diğer bilgileri ile yönlendirme yapabilirsiniz
        } catch (error) {
            if (error.response) {
                // Sunucudan bir yanıt geldiyse
                setError(error.response.data.message || 'Invalid credentials');
            } else {
                // Sunucuya ulaşamıyorsak
                setError('Network error. Please try again later.');
            }
        }
    };

    const handleRegister = async (e) => {
        e.preventDefault();
        setError(null);
        setSuccessMessage(null);

        try {
            const response = await axios.post('http://127.0.0.1:8000/api/register', {
                name,
                email,
                password,
                password_confirmation: passwordConfirmation,
            });
            console.log(response.data);
            setSuccessMessage('Registration successful! Please log in.');
            // Kayıttan sonra formu sıfırlamak için gerekli alanları temizleyebilirsiniz
            setName('');
            setEmail('');
            setPassword('');
            setPasswordConfirmation('');
        } catch (error) {
            if (error.response) {
                // Sunucudan bir yanıt geldiyse
                setError(error.response.data.message || 'An error occurred.');
            } else {
                // Sunucuya ulaşamıyorsak
                setError('Network error. Please try again later.');
            }
        }
    };

    return (
        <div className="container">
            <h2>{isRegistering ? 'Register' : 'Login'}</h2>
            <form onSubmit={isRegistering ? handleRegister : handleLogin}>
                {isRegistering && (
                    <input
                        type="text"
                        placeholder="Name"
                        value={name}
                        onChange={(e) => setName(e.target.value)}
                        required
                    />
                )}
                <input
                    type="email"
                    placeholder="Email"
                    value={email}
                    onChange={(e) => setEmail(e.target.value)}
                    required
                />
                <input
                    type="password"
                    placeholder="Password"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                    required
                />
                {isRegistering && (
                    <input
                        type="password"
                        placeholder="Confirm Password"
                        value={passwordConfirmation}
                        onChange={(e) => setPasswordConfirmation(e.target.value)}
                        required
                    />
                )}
                <button type="submit">{isRegistering ? 'Register' : 'Login'}</button>
            </form>
            <button onClick={() => setIsRegistering(!isRegistering)}>
                {isRegistering ? 'Already have an account? Login' : 'Don’t have an account? Register'}
            </button>
            {error && <div className="error">{error}</div>}
            {successMessage && <div className="success">{successMessage}</div>}
        </div>
    );
};

export default Login;
