import React, { useState } from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import "./Home.css";
import Login from "./Login";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";

const Home = () => {
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [isRegister, setIsRegister] = useState(false); // Başlangıç modu Giriş

  const handleOpenModal = () => {
    setIsModalOpen(true); // Modalı aç
  };

  const handleCloseModal = () => {
    setIsModalOpen(false); // Modalı kapat
  };

  const toggleMode = (mode) => {
    setIsRegister(mode === "register"); // Giriş veya Kayıt formunu ayarlar
  };

  return (
    <div className="container-fluid">
      {/* Ana sayfa içeriği */}
      <div className="row">
        <div className="col-md-2">
          <div className="advertisement">Reklam Alanı 1</div>
        </div>
        <div className="col-md-8">
          <button className="register-box" onClick={handleOpenModal}>
            Giriş Yap/Kayıt Ol
          </button>
        </div>
        <div className="col-md-2">
          <div className="advertisement">Reklam Alanı 2</div>
        </div>
      </div>

      {/* Modal */}
      {isModalOpen && (
        <div className="modal-overlay">
          <div className="modal-content">
            <button className="close-button" onClick={handleCloseModal}>
              &times;
            </button>
            <div className="auth-toggle d-flex justify-content-between">
              <button
                className={`auth-button ${!isRegister ? "active" : ""}`}
                onClick={() => toggleMode("login")}
              >
                Giriş Yap
              </button>
              <button
                className={`auth-button ${isRegister ? "active" : ""}`}
                onClick={() => toggleMode("register")}
              >
                Kayıt Ol
              </button>
            </div>
            <Login isRegister={isRegister} onRequestClose={handleCloseModal} />
          </div>
        </div>
      )}
    </div>
  );
};

export default Home;
