import React, { useState } from "react";
import axios from "axios";
import "./Login.css";

const Login = ({ isOpen, onRequestClose, isRegister, setIsLoggedIn }) => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [name, setName] = useState("");
  const [passwordConfirmation, setPasswordConfirmation] = useState("");
  const [error, setError] = useState(null);
  const [successMessage, setSuccessMessage] = useState(null);

  if (!isOpen) {
    return null; // Eğer modal açık değilse, bileşeni hiç render etme
  }

  const handleLogin = async (e) => {
    e.preventDefault();
    setError(null);
    setSuccessMessage(null);

    try {
      const response = await axios.post("http://127.0.0.1:8000/api/login", {
        email,
        password,
      });
      setSuccessMessage("Giriş başarılı!");
      setIsLoggedIn(true); // Giriş başarılı olduğunda durumu güncelle
      onRequestClose(); // Modalı kapat
    } catch (error) {
      setError(error.response?.data.message || "Giriş hatalı.");
    }
  };

  const handleRegister = async (e) => {
    e.preventDefault();
    setError(null);
    setSuccessMessage(null);

    try {
      const response = await axios.post("http://127.0.0.1:8000/api/register", {
        name,
        email,
        password,
        password_confirmation: passwordConfirmation,
      });
      setSuccessMessage("Kayıt başarılı!");
      onRequestClose(); // Kayıt başarılı olduğunda modalı kapat
    } catch (error) {
      setError(error.response?.data.message || "Kayıt hatası.");
    }
  };

  return (
    <div className="login-modal p-4">
      <h2 className="text-center">{isRegister ? "Kayıt Ol" : "Giriş Yap"}</h2>
      {error && <div className="alert alert-danger">{error}</div>}
      {successMessage && (
        <div className="alert alert-success">{successMessage}</div>
      )}

      <form onSubmit={isRegister ? handleRegister : handleLogin} className="mt-4">
        {isRegister && (
          <div className="mb-3">
            <input
              type="text"
              className="form-control"
              placeholder="Adınız"
              value={name}
              onChange={(e) => setName(e.target.value)}
              required
            />
          </div>
        )}
        <div className="mb-3">
          <input
            type="email"
            className="form-control"
            placeholder="E-posta"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
        </div>
        <div className="mb-3">
          <input
            type="password"
            className="form-control"
            placeholder="Şifre"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
        </div>
        {isRegister && (
          <div className="mb-3">
            <input
              type="password"
              className="form-control"
              placeholder="Şifre Onayı"
              value={passwordConfirmation}
              onChange={(e) => setPasswordConfirmation(e.target.value)}
              required
            />
          </div>
        )}
        <button type="submit" className="btn btn-primary w-100">
          {isRegister ? "Kayıt Ol" : "Giriş Yap"}
        </button>
      </form>
    </div>
  );
};

export default Login;
