import React, { useState } from "react";

const Login = ({ isOpen, onRequestClose, isRegister, setIsLoggedIn }) => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [isChecked, setIsChecked] = useState(false);
  
  const handleCheckboxChange = () => {
    setIsChecked(!isChecked);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Burada form gönderimi işlemlerini yapabilirsiniz
    // Örnek: API çağrısı yaparak giriş veya kayıt işlemi
    console.log("Email:", email);
    console.log("Password:", password);
    console.log("Agreed to terms:", isChecked);

    // Giriş başarılıysa isLoggedIn durumunu güncelle
    setIsLoggedIn(true);
    onRequestClose(); // Modalı kapat
  };

  return (
    <div>
      <h2>{isRegister ? "Kayıt Ol" : "Giriş Yap"}</h2>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label htmlFor="email">Email:</label>
          <input
            type="email"
            id="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
            className="form-control"
          />
        </div>
        <div className="form-group">
          <label htmlFor="password">Şifre:</label>
          <input
            type="password"
            id="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
            className="form-control"
          />
        </div>
        {isRegister && (
          <div className="form-group">
            <input
              type="checkbox"
              id="terms"
              className="checkbox"
              checked={isChecked}
              onChange={handleCheckboxChange}
            />
            <label htmlFor="terms" className="link">
              Kullanım şartlarını kabul ediyorum
            </label>
          </div>
        )}
        <button type="submit" className="btn btn-primary">
          {isRegister ? "Kayıt Ol" : "Giriş Yap"}
        </button>
      </form>
      <button className="btn btn-link" onClick={onRequestClose}>
        İptal
      </button>
    </div>
  );
};

export default Login;
