import React, { useState } from "react";
import axios from "axios";
import "./Login.css";

const Login = ({ isLoginMode, onRequestClose, setIsLoggedIn }) => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [name, setName] = useState("");
  const [passwordConfirmation, setPasswordConfirmation] = useState("");
  const [error, setError] = useState(null);
  const [successMessage, setSuccessMessage] = useState(null);

  const handleLogin = async (e) => {
    e.preventDefault();
    setError(null);
    setSuccessMessage(null);

    try {
      const response = await axios.post("http://127.0.0.1:8000/api/login", {
        email,
        password,
      });
      setSuccessMessage("Login successful!");
      setIsLoggedIn(true);
      onRequestClose();
    } catch (error) {
      setError(error.response?.data.message || "Invalid credentials");
    }
  };

  const handleRegister = async (e) => {
    e.preventDefault();
    setError(null);
    setSuccessMessage(null);

    try {
      const response = await axios.post("http://127.0.0.1:8000/api/register", {
        name,
        email,
        password,
        password_confirmation: passwordConfirmation,
      });
      setSuccessMessage("Registration successful!");
      onRequestClose();
    } catch (error) {
      setError(error.response?.data.message || "Registration failed");
    }
  };

  return (
    <div className="login-modal p-4">
      <h2 className="text-center">{isLoginMode ? "Giriş Yap" : "Kayıt Ol"}</h2>
      {error && <div className="alert alert-danger">{error}</div>}
      {successMessage && (
        <div className="alert alert-success">{successMessage}</div>
      )}

      <form onSubmit={isLoginMode ? handleLogin : handleRegister} className="mt-4">
        {!isLoginMode && (
          <div className="mb-3">
            <input
              type="text"
              className="form-control"
              placeholder="Adınız"
              value={name}
              onChange={(e) => setName(e.target.value)}
              required
            />
          </div>
        )}
        <div className="mb-3">
          <input
            type="email"
            className="form-control"
            placeholder="E-mail"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
        </div>
        <div className="mb-3">
          <input
            type="password"
            className="form-control"
            placeholder="Şifre"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
        </div>
        {!isLoginMode && (
          <div className="mb-3">
            <input
              type="password"
              className="form-control"
              placeholder="Şifreyi Onayla"
              value={passwordConfirmation}
              onChange={(e) => setPasswordConfirmation(e.target.value)}
              required
            />
          </div>
        )}
        <button type="submit" className="btn btn-primary w-100">
          {isLoginMode ? "Giriş Yap" : "Kayıt Ol"}
        </button>
      </form>
    </div>
  );
};

export default Login;
