import React, { useState } from 'react';
import axios from 'axios';
import './Login.css'; // CSS dosyasını içe aktar

const Login = ({ isOpen, onRequestClose, isRegister }) => {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [name, setName] = useState('');
    const [passwordConfirmation, setPasswordConfirmation] = useState('');
    const [error, setError] = useState(null);
    const [successMessage, setSuccessMessage] = useState(null);

    const handleLogin = async (e) => {
        e.preventDefault();
        setError(null);
        setSuccessMessage(null);

        try {
            const response = await axios.post('http://127.0.0.1:8000/api/login', {
                email,
                password,
            });
            console.log(response.data);
            setSuccessMessage('Login successful!');
            // Giriş başarılı olduğunda modalı kapat
            onRequestClose();
        } catch (error) {
            if (error.response) {
                setError(error.response.data.message || 'Invalid credentials');
            } else {
                setError('Network error. Please try again later.');
            }
        }
    };

    const handleRegister = async (e) => {
        e.preventDefault();
        setError(null);
        setSuccessMessage(null);

        try {
            const response = await axios.post('http://127.0.0.1:8000/api/register', {
                name,
                email,
                password,
                password_confirmation: passwordConfirmation,
            });
            console.log(response.data);
            setSuccessMessage('Registration successful! Please log in.');
            setName('');
            setEmail('');
            setPassword('');
            setPasswordConfirmation('');
        } catch (error) {
            if (error.response) {
                setError(error.response.data.message || 'An error occurred.');
            } else {
                setError('Network error. Please try again later.');
            }
        }
    };

    if (!isOpen) return null; // Modal kapalıysa hiçbir şey gösterme

    return (
        <div className="modal">
            <div className="container"> {/* Yeni kapsayıcı */}
                <h2>{isRegister ? 'Register' : 'Login'}</h2>
                <form onSubmit={isRegister ? handleRegister : handleLogin}>
                    {isRegister && (
                        <input
                            type="text"
                            placeholder="Name"
                            value={name}
                            onChange={(e) => setName(e.target.value)}
                            required
                        />
                    )}
                    <input
                        type="email"
                        placeholder="Email"
                        value={email}
                        onChange={(e) => setEmail(e.target.value)}
                        required
                    />
                    <input
                        type="password"
                        placeholder="Password"
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                        required
                    />
                    {isRegister && (
                        <input
                            type="password"
                            placeholder="Confirm Password"
                            value={passwordConfirmation}
                            onChange={(e) => setPasswordConfirmation(e.target.value)}
                            required
                        />
                    )}
                    <button type="submit">{isRegister ? 'Register' : 'Login'}</button>
                </form>
                <button className="close-button" onClick={onRequestClose}>Close</button>
                {error && <div className="error">{error}</div>}
                {successMessage && <div className="success">{successMessage}</div>}
            </div>
        </div>
    );
    
};

export default Login;
